import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Button } from '@mui/base/Button';


function BookingForm({hallName, seatNum})
{
    let daysSet = new Set;

    const handleDayClick = (e, i) => {
        if (e.target.checked)
        {
            days.add(i);
        }
        else
        {
            days.delete(i);
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = "http://localhost:5000/halls/" + hallName + "/" + seatNum;
        let res = await fetch(url, {
            method: "POST",
            body: JSON.stringify({
                username: "leema",
                days: Array.from(daysSet),
                months: [100]
            })
        })
    };

    return(
        <div>
            <h1>Book Your Seat</h1>
            <h2>Valid till the end of this year</h2>
            <form onSubmit={handleSubmit}>
                <fieldset class="weekDays-selector" name='days'>
                    <legend name='days'>Choose Your Days</legend>
                    <input type="checkbox" id="mon" value={0} className="weekday" onClick={(e) => handleDayClick(e.target.value)} />
                    <label for="mon">M</label>
                    <input type="checkbox" id="tue" value={1} className="weekday" onClick={(e) => handleDayClick(e.target.value)} />
                    <label for="tue">T</label>
                    <input type="checkbox" id="wed" value={2} className="weekday" onClick={(e) => handleDayClick(e.target.value)} />
                    <label for="wed">W</label>
                    <input type="checkbox" id="thu" value={3} className="weekday" onClick={(e) => handleDayClick(e.target.value)}/>
                    <label for="thu">T</label>
                    <input type="checkbox" id="fri" value={4} className="weekday" onClick={(e) => handleDayClick(e.target.value)}/>
                    <label for="fri">F</label>
                    <input type="checkbox" id="sat" value={5} className="weekday" onClick={(e) => handleDayClick(e.target.value)} />
                    <label for="sat">S</label>
                    <input type="checkbox" id="sun" value={6} className="weekday" onClick={(e) => handleDayClick(e.target.value)} />
                    <label for="sun" className='text-orange-600'>S</label>
                </fieldset>
                <input type='text' value='aleema' name="username" />
                <Button className="bg-green-600 rounded-md py-1 px-4..." type='submit'>Book</Button>
            </form>
        </div>
    );
}

export default BookingForm;
