import * as React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import {useState} from 'react';
import Header from './Header';
import HallDisplay from './HallDisplay';
import NavBar from './NavBar';

const drawerWidth = 240;

function Dashboard(props) {
  const { window } = props;
  const [selectedhall, setSelectedHall] = useState(null);
  const [mobileOpen, setMobileOpen] = useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  console.log("window prop: " + window);

  const navProps = {mobileOpen, setMobileOpen, setSelectedHall, drawerWidth, window}

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <Header handleDrawerToggle={handleDrawerToggle} />
      <NavBar {...navProps} />
      {selectedhall ? <HallDisplay drawerWidth={drawerWidth} selectedhall={selectedhall}/> : "Select Hall"}
    </Box>
  );
}

Dashboard.propTypes = {
  window: PropTypes.func,
};

export default Dashboard;
