import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Kayak from './hallPlans/Kayak';
import Typography from '@mui/material/Typography';
import { HallContext } from '../contexts/HallContext';
import { useState, useEffect, useMemo } from 'react';

function HallDisplay({drawerWidth, selectedhall})
{
    const [hallData, setHallData] = useState(null);
    const seatsData = useMemo(() => hallData ? hallData.seats : null , [hallData]);
    console.log("hallData seats: ");

    useEffect(() => {
      const url = "http://localhost:5000/halls/" + selectedhall;
      fetch(url)
      .then(response => response.json())
      .then(data => {setHallData(data); console.log(data.seats)})
      .catch(err => console.log(err));
    })

    return (
        <Box
        component="main"
        sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` } }}
        >
        <Toolbar />
        {hallData && 
        <HallContext.Provider value={seatsData}>
          <Kayak />
        </HallContext.Provider>
        }
      </Box>
    )

}

export default HallDisplay;
