import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Button } from '@mui/base/Button';
import CheckboxFormInput from './CheckboxFormInput';


function BookingForm({hallName, seatNum, setBookingSuccess})
{
    let daysSet = new Set;
    let monthsSet = new Set;

    const handleDayClick = (i, checked) => {
        if (checked)
        {
            daysSet.add(Number(i));
        }
        else
        {
            daysSet.delete(Number(i));
        }
    }

    const handleMonthClick = (i, checked) => {
        if (checked)
        {
            monthsSet.add(Number(i));
        }
        else
        {
            monthsSet.delete(Number(i));
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = "http://localhost:5000/halls/" + hallName + "/" + seatNum;
        let res = await fetch(url, {
            method: "POST",
            body: JSON.stringify({
                username: "leema",
                days: Array.from(daysSet),
                months: Array.from(monthsSet)
            }),
            headers: {
                'Content-Type': 'application/json'
              },
        });

        if (res.status == 200) { setBookingSuccess(true);} else { setBookingSuccess(false);} }

        const dayInput = ['M', 'T', 'W', 'Th', 'F', 'S','S'].map( (day, index) =>
            <CheckboxFormInput id={"d" + index} value={index} className="weekday" handleClick={handleDayClick} label={day} />
        );

        const monthInput = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'].map( (month, index) => 
        <CheckboxFormInput id={"m" + index} value={index} className="month" handleClick={handleMonthClick} label={month} />
        );

        console.log(dayInput);

    return(
        <div>
            <h1>Book Your Seat</h1>
            <h2>Valid till the end of this year</h2>
            <form onSubmit={handleSubmit}>
                <fieldset class="weekDays selector" name='days'>
                    <legend>Choose Your Days</legend>
                    {dayInput}
                </fieldset>
                <fieldset class="months selector" name='days'>
                    <legend>Choose Your Months</legend>
                    {monthInput}
                </fieldset>
                <Button className="bg-green-600 rounded-md py-1 px-4..." type='submit'>Book</Button>
            </form>
        </div>
    );
}

export default BookingForm;

