function FormInput({label, type})
{
    const [text, setText] = useState('');

    return (
        <label>
            {label}
            <input required name={label} type={type} placeholder={'enter ' + label} value={text} onChange={(e) => setText(e.target.value)} />
        </label>
    )

}
