import Box from '@mui/material/Box';
import HallsDrawer from './HallsDrawer';
import Drawer from '@mui/material/Drawer';

function  NavBar({mobileOpen, handleDrawerToggle, halls, setSelectedHall, drawerWidth, window})
{
  const container = window !== undefined ? () => window().document.body : undefined;

  useEffect( () => {
    fetch('http://localhost:5000/halls')
    .then((response) => response.json())
    .then(json => setHalls(json.halls))
    .catch((err) => setHalls(["error. try later."]));
  },[])

    return (
        <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        aria-label="mailbox folders"
      >
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Drawer
          container={container}
          variant="temporary"
          open={mobileOpen}
          onClose={handleDrawerToggle}
          ModalProps={{
            keepMounted: true, // Better open performance on mobile.
          }}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
        >
        <HallsDrawer halls={halls} setSelectedHall={setSelectedHall} />
        </Drawer>
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', sm: 'block' },
            '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
          }}
          open
        >
        <HallsDrawer halls={halls} setSelectedHall={setSelectedHall} />
        </Drawer>
      </Box>
    );
}

export default NavBar;
