import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Button from '@mui/material/Button';
import CheckboxFormInput from './CheckboxFormInput';
import { useState } from 'react';
import { Toolbar } from '@mui/material';


function BookingForm({hallName, seatNum, setOpen})
{
    let daysSet = new Set;
    let monthsSet = new Set;
    const [dayError, setDayError] = useState(null);
    const [monthError, setMonthError] = useState(null);
    const [response, setResponse] = useState(null);


    const handleDayClick = (i, checked) => {
        setDayError(null);
        if (checked)
        {
            daysSet.add(Number(i));
        }
        else
        {
            daysSet.delete(Number(i));
        }
    };

    const handleMonthClick = (i, checked) => {
        setMonthError(null);
        if (checked)
        {
            monthsSet.add(Number(i));
        }
        else
        {
            monthsSet.delete(Number(i));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (daysSet.size == 0)
        {
            setDayError("Pick at least one day.");
            return;
        }
        else if (monthsSet.size == 0)
        {
            setMonthError("Pick at least one month.");
            return;
        }
        const url = "http://localhost:5000/halls/" + hallName + "/" + seatNum;
        const res = await fetch(url, {
            method: "POST",
            body: JSON.stringify({
                username: "faisal",
                days: (daysSet.size == 7) ? [100] : Array.from(daysSet),
                months: (monthsSet.size == 12) ? [100] : Array.from(monthsSet)
            }),
            headers: {
                'Content-Type': 'application/json'
              },
        });
        res.text()
        .then(msg => setResponse(msg));
    }

        const dayInput = ['M', 'T', 'W', 'Th', 'F', 'S', 'S'].map( (day, index) =>
            <CheckboxFormInput id={"d" + index} value={(index==6) ? 0 : index+1} className="weekday" handleClick={handleDayClick} label={day} />
        );

        const monthInput = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'].map( (month, index) => 
            <CheckboxFormInput id={"m" + index} value={index} className="month" handleClick={handleMonthClick} label={month} />
        );

        if (!response){
        return(
            <div>
                <h1>Book Your Seat</h1>
                <h2>Valid till the end of this year</h2>
                <form onSubmit={handleSubmit}>
                    <fieldset class="weekDays selector" name='days'>
                        <legend>Choose Your Days</legend>
                        {dayInput}
                    </fieldset>
                    {dayError && <span className='input-error'>{dayError}</span>}
                    <fieldset class="months selector" name='days'>
                        <legend>Choose Your Months</legend>
                        {monthInput}
                    </fieldset>
                    {monthError && <span className='input-error'>{monthError}</span>}
                    <Toolbar />
                    <Button variant="contained" type='submit' className='booking-button'>Book</Button>
                </form>
            </div>
        );            
        }
        else{
            return (
                <div>
                    <h1>{response}</h1>
                    <Button variant="contained" onClick={() => {setOpen(false); setResponse(null); window.location.reload();}}>OK</Button>
                </div>
            )
        }

}

export default BookingForm;
