import * as React from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import InboxIcon from '@mui/icons-material/MoveToInbox';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import MailIcon from '@mui/icons-material/Mail';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import {useState, useEffect} from 'react';
import Header from './Header';
import HallDisplay from './HallDisplay';
import NavBar from './NavBar';

const drawerWidth = 240;

function ResponsiveDrawer(props) {
  const { window } = props;
  const [halls, setHalls] = useState(['Choose Hall']);
  const [mobileOpen, setMobileOpen] = useState(false);
  const [selectedhall, setSelectedHall] = useState(halls[0]);

  useEffect( () => {
    fetch('http://localhost:5000/halls')
    .then((response) => response.json())
    .then(json => setHalls(json.halls))
    .catch((err) => setHalls(["error. try later."]));
  },[])

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };


  const navProps = { mobileOpen, handleDrawerToggle, halls, setSelectedHall, drawerWidth, window}

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      <Header handleDrawerToggle={handleDrawerToggle} />
      <NavBar {...navProps} />
      <HallDisplay drawerWidth={drawerWidth} selectedhall={selectedhall} />
    </Box>
  );
}

ResponsiveDrawer.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default ResponsiveDrawer;
