import { useState } from 'react';
import FormInput from '../components/formInput';
import axios from 'axios';

function Login({loggedIn, setLoggedIn})
{
    if (loggedIn)
    {
        return(
            <Navigate to="/home" /> 
        )
    }
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const reqBody = {
            email: e.target.email.value,
            password: e.target.password.value
        };
        await axios.post('http://localhost:5000/login', reqBody)
        .then(() => {setLoggedIn(true); setError(null);})
        .catch((err) => setError(err.response.data));
    };

    return (
        <form onSubmit={handleSubmit} key='login-form' >
            <FormInput type='email' label='Email' key='email' />
            <FormInput type='password' label='Password' key='pasword' />
            {error && <p className='error-msg'>{error}</p>}
            <button type='submit'>Login</button>
        </form>
    )
}

export default Login;
