import Seat from './Seat';
import { HallContext } from '../contexts/HallContext';

function HallRow({totalSeats, startSeatNum})
{
    const seats = [];
    const seatsPerRow = totalSeats/2;
    for (let i=startSeatNum; i<= (startSeatNum+seatsPerRow); i++)
    {
        seats.push(i);
    }

    return (
        <div className='row'>
            <div>
                {seats.map( (index) =>
                    <Seat seatNum={index} key={index} />
                )}
            </div>
            <div>
                {seats.map( index =>
                    <Seat seatNum={index + seatsPerRow}  key={index + seatsPerRow}/>
                )}
            </div>
        </div>
    )
}

export default HallRow;
