import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import { Button } from '@mui/base/Button';


function BookingForm({hallName, seatNum})
{
    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = "http://localhost:5000/halls/" + hallName + "/" + seatNum;
        let res= await fetch(url, {
            method: "POST",
            body: JSON.stringify({
                username: "leema",

            })
        })

    }

    return(
        <div>
            <h1>Book Your Seat</h1>
            <h2>Valid till the end of this year</h2>
            <form action={url} method="post">
                <fieldset class="weekDays-selector" name='days'>
                    <legend name='days'>Choose Your Days</legend>
                    <input type="checkbox" id="mon" value={0} className="weekday" />
                    <label for="mon">M</label>
                    <input type="checkbox" id="tue" value={1} className="weekday" />
                    <label for="tue">T</label>
                    <input type="checkbox" id="wed" value={2} className="weekday" />
                    <label for="wed">W</label>
                    <input type="checkbox" id="thu" value={3} className="weekday" />
                    <label for="thu">T</label>
                    <input type="checkbox" id="fri" value={4} className="weekday" />
                    <label for="fri">F</label>
                    <input type="checkbox" id="sat" value={5} className="weekday" />
                    <label for="sat">S</label>
                    <input type="checkbox" id="sun" value={6} className="weekday" />
                    <label for="sun" className='text-orange-600'>S</label>
                </fieldset>
                <input type='text' value='aleema' name="username" />
                <Button className="bg-green-600 rounded-md py-1 px-4..." type='submit'>Book</Button>
            </form>
        </div>
    );
}

export default BookingForm;
