import './App.css';
import { Route, Routes, Navigate } from "react-router-dom";
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/LoginPage';
import { http } from "./http_index";

function App() {
  const [user, setUser] = useState(null);
  console.log("user in app: ", user);
  const navigate = useNavigate();

  useEffect( async () => {
    const ignore = false;
    const appToken = localStorage.getItem("appToken");
    if (appToken){
      http.post('/login', {appToken})
      .then( userData => {
        if (!ignore)
        {
          console.log(userData);
          localStorage.setItem("appJWT", userData.data.appToken);
          localStorage.setItem("googleToken", userData.data.googleToken);
          setUser(userData.data.userName);
          navigate('/home');          
        }
      })
    }

    return () => {ignore=true};
  }, [])

  return(
      <Routes>
        <Route exact path="/" element={ user ? <Navigate to="/login" /> : <Navigate to="/home" /> } />
        <Route path="/login" element={ user ? <Navigate to="/home" /> : < Login setUser={setUser} />} />
        <Route path="/home" element={ user? < Home user={user} /> : <Navigate to="/login" /> } />
      </Routes>
  )
}

export default App;
