import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Kayak from './hallPlans/Kayak';
import Typography from '@mui/material/Typography';
import { HallContext } from '../contexts/HallContext';
import { useState } from 'react';

function HallDisplay({drawerWidth, selectedhall})
{

    const setHallData = async () => {
    const url = "https://localhost:5000/" + selectedhall;
    const data = await fetch(url)
    .then((response) => response.json())
    .then( data => hallData = data)
    .catch(err => console.log(err));
    return data;
    }
    const hallData = setHallData();

    return (
        <Box
        component="main"
        sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` } }}
        >
        <Toolbar />
        <HallContext.Provider value={hallData}>
        {selectedhall=="Kayak" ? <Kayak /> : selectedhall}
        </HallContext.Provider>
      </Box>
    )

}

export default HallDisplay;
