import { Button } from '@mui/base/Button';
import Tooltip from '@mui/material/Tooltip';
import Modal from '@mui/material/Modal';
import { useState } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';



function Seat({seatNum, data})
{
    const [selected, setSelected] = useState(false);
    const handleOpen = () => setSelected(true);
    const handleClose = () => setSelected(false);

    const available = data.available;
    console.log("data for seat ", seatNum, ": " ,data);
    let style = "seat rounded-md";
    style += available ? " border-solid border-teal-300 bg-transparent border-2 hover:bg-teal-100 active:bg-teal-700" :  " bg-teal-300";

    if (available)
    {
        return (
            <>
            <Button className={style} disabled={!available} onClick={handleOpen}>{data.seatNum}</Button>
            <Modal
            open={selected}
            onClose={handleClose}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
        >
                <Typography id="modal-modal-title" variant="h6" component="h2">
                Text in a modal
                </Typography>
            </Modal>
        </>
        )
    }
    else
    {
        return (
            <Tooltip title={data.bookings[0].username}>
                <Button className={style} disabled={!available}>{data.seatNum}</Button>
            </Tooltip>
        )        
    }

}

export default Seat;
